var link=document.querySelector("#feedback-form"),popup=document.querySelector(".feedback"),close=popup.querySelector(".feedback__close");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("opened")}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("opened"),popup.classList.remove("feedback-error")}),window.addEventListener("keydown",function(e){27===e.keyCode&&(e.preventDefault(),popup.classList.contains("opened")&&(popup.classList.remove("opened"),popup.classList.remove("feedback-error")))});